import "./confess.scss";
import Image from "../../assets/img.png";
import Map from "../../assets/map.png";
import Friend from "../../assets/friend.png";
import { useContext, useState } from "react";
import { AuthContext } from "../../context/authContext";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { makeRequest } from "../../axios";
import karlosImage from "../../1690467391565karlos.jpg";
const Confess = () => {
  const file = useState(null);
  const [confession, setConfession] = useState("");
  const [title, setTitle] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [anonymous, setAnonymous] = useState(false);

  

  const { currentUser } = useContext(AuthContext);

  const queryClient = useQueryClient();

  const mutation = useMutation(
    (newConfession) => {
      return makeRequest.post("/confessions", newConfession);
    },
    {
      onSuccess: () => {
        // Invalidate and refetch
        queryClient.invalidateQueries(["confessions"]);
      },
    }
  );
  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleClick = async (e) => {
    e.preventDefault();
    // let imgUrl = "";
    // if (file) imgUrl = await upload();
    mutation.mutate({ confession });
    setConfession("");
    // setFile(null);
    // setShowModal(true);
  };

  return (
    <div className="confess">
      <div className="container">
        <div className="top">
          <div className="left">
            <img src={currentUser.profilePic} alt="" />
            <input
              type="text"
              placeholder={`Click to Add a Confession`} 
              onClick={() => setShowModal(true)} 
              onChange={(e) => setConfession(e.target.value)}
              value={confession}
            />
          </div>
          <div className="right">
            <button onClick={handleClick}> + </button>
          </div>
        </div>
      </div>{showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Write Your Confession</h2>
            <input
              type="text"
              placeholder="Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            <textarea
              placeholder="Confession"
              value={confession}
              onChange={(e) => setConfession(e.target.value)}
            />
            <div>
              <label>
                <input
                  type="checkbox"
                  checked={anonymous}
                  onChange={() => setAnonymous(!anonymous)}
                />
                Anonymous
              </label>
            </div>
            <div className="image-buttons">
              {/* Image buttons here */}
              <button>
                <img src={yourImageSrc} alt="Button 1" />
              </button>
              <button>
                <img src={yourImageSrc} alt="Button 2" />
              </button>
              <button>
                <img src={yourImageSrc} alt="Button 3" />
              </button>
            </div>
            <button onClick={handleCloseModal}>Close</button>
            <button onClick={handleClick}>Submit Confession</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Confess;
